//INSTANTIATE AN ENEMY
void instantiateEnemy()
{
    i = 0;

    for (j = 0; j != maxEnemies; j++) {

        if ((eActive[j] == 0) && (i == 0)) {
            i = 1;

            switch (round) {
                //JUST WASPS
                case 1:
                    eType[j] = 2;
                    break;

                // WASPS AND BOIRDS
                case 2:
                    eType[j] = 1 + (DIV_REG % 2);
                    break;

                //EVERYTHING
                case 3:
                    eType[j] = 1 + (DIV_REG % 3);
                    break;

                //MAKE EM ALL A BIT FASTER
                case 4:
                case 5:
                case 6:
                    eType[j] = 1 + (DIV_REG % 3);
                    mSpeed = 1;
                    break;

                default:
                    eType[j] = 1 + (DIV_REG % 3);
                    break;
            }

            // MOTH
            //eType[j] = 1;

            // WASP


            if (fakeRandom > 128) {
                tileOffset = 4;
                eX[j] = 0;
                eSubX[j] = 1;
            } else {
                tileOffset = 0;
                eX[j] = 160;
                eSubX[j] = 0;
            }

            eActive[j] = 1;

            switch (eType[j]) {

                case 1:
                    eSubY[j] = 1;       //DOWNWARDS INITIALLY
                    eHP[j] = 3;
                    eY[j] = 32 + (fakeRandom / 3);
                    k = (j << 2) + 9;
                    set_sprite_tile(k, 0x68 - tileOffset);
                    k++;
                    set_sprite_tile(k, 0x69 - tileOffset);
                    k++;
                    set_sprite_tile(k, 0x70 - tileOffset);
                    k++;
                    set_sprite_tile(k, 0x71 - tileOffset);
                    break;

                //WASP
                case 2:
                    eHP[j] = 2;
                    eTime[j] = fakeRandom;

                    eTargetY[j] = 16 + (fakeRandom / 4);
                    eY[j] = 32 + (fakeRandom / 4);
                    k = (j << 2) + 9;
                    set_sprite_tile(k, 0x28 + tileOffset);
                    k++;
                    set_sprite_tile(k, 0x29 + tileOffset);
                    k++;
                    set_sprite_tile(k, 0x30 + tileOffset);
                    k++;
                    set_sprite_tile(k, 0x31 + tileOffset);
                    break;

                //SPIDER - DESCEND FROM CEILING, THAN CRAWL OVER TO THE BEEHIVE
                case 3:
                    eHP[j] = 3;
                    eX[j] = 146 - scX;
                    eY[j] = 0;
                    eSubY[j] = 0;   //WHICH DIRECTION
                    eSubX[j] = 0;   // ANIMATE FRAME TIMER
                    k = (j << 2) + 9;
                    set_sprite_tile(k, 0xe4);
                    set_sprite_tile(k + 1, 0xe5);
                    set_sprite_tile(k + 2, 0xec);
                    set_sprite_tile(k + 3, 0xed);

            }
            //BSPIDER
            //eType[j] = 3;

            //WEIRD BLOB THING
            //eType[j] = 4;
        }

    }

}


//UPDATE ENEMIES
void updateEnemies()
{

    for (j = 0; j != maxEnemies; j++) {
        if (eActive[j] == 1) {
            k = (j << 2) + 9;

            switch (eType[j]) {
                //BIRD
                case 1:
                    if (eSubY[j] > 1) {

                        eSubY[j] --;
                        if (eSubY[j] == 2) {
                            eSubY[j] = 0;
                        }

                    } else {
                        eTime[j]++;
                        //ANIMATE THE BIRDS WHEN THEY ARE FLYING
                        if ((eTime[j] % 16) == 8) {

                            if ( eSubX[j] == 1) {
                                set_sprite_tile(k, 0x68);
                                set_sprite_tile(k + 1, 0x69);
                                set_sprite_tile(k + 2, 0x70);
                                set_sprite_tile(k + 3, 0x71);
                            } else {
                                set_sprite_tile(k, 0x64);
                                set_sprite_tile(k + 1, 0x65);
                                set_sprite_tile(k + 2, 0x6C);
                                set_sprite_tile(k + 3, 0x6D);
                            }

                        }

                        if ((eTime[j] % 16) == 0) {

                            if ( eSubX[j] == 1) {
                                set_sprite_tile(k, 0x6A);
                                set_sprite_tile(k + 1, 0x6B);
                                set_sprite_tile(k + 2, 0x72);
                                set_sprite_tile(k + 3, 0x73);
                            } else {
                                set_sprite_tile(k, 0x66);
                                set_sprite_tile(k + 1, 0x67);
                                set_sprite_tile(k + 2, 0x6E);
                                set_sprite_tile(k + 3, 0x6F);
                            }

                        }

                        switch (eSubX[j]) {
                            case 0:
                                eX[j] -= 2;
                                break;
                            default:
                                eX[j] += 2;
                        }

                        switch (eSubY[j]) {
                            case 0:
                                eY[j] -= 2;

                                if (eY[j] < 16) {
                                    eSubY[j] = 1;
                                }

                                break;

                            case 1:
                                eY[j]++;

                                if (eY[j] >= 112) {
                                    eSubY[j] = 96;
                                }

                                break;
                        }
                    }
                    move_sprite(k, eX[j], eY[j]);
                    k++;
                    move_sprite(k, eX[j] + 8, eY[j]);
                    k++;
                    move_sprite(k, eX[j], eY[j] + 8);
                    k++;
                    move_sprite(k, eX[j] + 8, eY[j] + 8);
                    break;
                //WASP

                case 2:
                    eTime[j]++;

                    if (eTime[j] == 40) {
                        eTargetY[j] = 16 + (DIV_REG >> 2) + (DIV_REG >> 3);
                        //LAUNCH A SHOT AT THE PLAYER
                    }

                    if (eTime[j] > 128) {
                        if (eX[j] < pX) {
                            tileOffset = 0;
                            set_sprite_tile(k, 0x28 + tileOffset);
                            set_sprite_tile(k + 1, 0x29 + tileOffset);
                            set_sprite_tile(k + 2, 0x30 + tileOffset);
                            set_sprite_tile(k + 3, 0x31 + tileOffset);

                            eX[j] += (1 + mSpeed);

                        } else {
                            eX[j] -= (1 + mSpeed);
                            tileOffset = 4;
                            set_sprite_tile(k, 0x28 + tileOffset);
                            set_sprite_tile(k + 1, 0x29 + tileOffset);
                            set_sprite_tile(k + 2, 0x30 + tileOffset);
                            set_sprite_tile(k + 3, 0x31 + tileOffset);

                        }
                    }

                    if (eTargetY[j] > eY[j]) {
                        eY[j] += 2;
                    } else {
                        eY[j] -= 2;
                    }

                    move_sprite(k, eX[j], eY[j]);
                    k++;
                    move_sprite(k, eX[j] + 8, eY[j]);
                    k++;
                    move_sprite(k, eX[j], eY[j] + 8);
                    k++;
                    move_sprite(k, eX[j] + 8, eY[j] + 8);
                    break;

                //SPIDER
                case 3:
                    /*
                    eHP[j] = 3;
                    eX[j] = 146-scX;
                    eY[j] = 0;
                    */

                    eSubX[j]++;




                    switch (eSubY[j]) {
                        case 0:
                            eY[j]++;

                            if ((eSubX[j] % 16) == 8) {
                                set_sprite_tile(k, 0xe4);
                                set_sprite_tile(k + 1, 0xe5);
                                set_sprite_tile(k + 2, 0xec);
                                set_sprite_tile(k + 3, 0xed);
                            }

                            if ((eSubX[j] % 16) == 0) {
                                set_sprite_tile(k, 0xe6);
                                set_sprite_tile(k + 1, 0xe7);
                                set_sprite_tile(k + 2, 0xee);
                                set_sprite_tile(k + 3, 0xef);
                            }

                            if (eY[j] == 112) {
                                eSubY[j] = 1;
                                eSubX[j] = (fakeRandom + DIV_REG) % 2;
                            }

                            break;

                        case 1:
                            if (eSubX[j] == 1) {

                                if ((eSubX[j] % 16) == 8) {
                                    set_sprite_tile(k, 0xe8);
                                    set_sprite_tile(k + 1, 0xe9);
                                    set_sprite_tile(k + 2, 0xf0);
                                    set_sprite_tile(k + 3, 0xf1);
                                }

                                if ((eSubX[j] % 16) == 0) {
                                    set_sprite_tile(k, 0xea);
                                    set_sprite_tile(k + 1, 0xeb);
                                    set_sprite_tile(k + 2, 0xf2);
                                    set_sprite_tile(k + 3, 0xf3);
                                }

                                eX[j]--;
                            } else {

                                if ((eSubX[j] % 16) == 8) {
                                    set_sprite_tile(k, 0xf4);
                                    set_sprite_tile(k + 1, 0xf5);
                                    set_sprite_tile(k + 2, 0xf8);
                                    set_sprite_tile(k + 3, 0xf9);
                                }

                                if ((eSubX[j] % 16) == 0) {
                                    set_sprite_tile(k, 0xf6);
                                    set_sprite_tile(k + 1, 0xf7);
                                    set_sprite_tile(k + 2, 0xfa);
                                    set_sprite_tile(k + 3, 0xfb);
                                }


                                eX[j]++;
                            }

                            break;

                    }
                    move_sprite(k, eX[j], eY[j]);
                    k++;
                    move_sprite(k, eX[j] + 8, eY[j]);
                    k++;
                    move_sprite(k, eX[j], eY[j] + 8);
                    k++;
                    move_sprite(k, eX[j] + 8, eY[j] + 8);
                    break;
            }

        }
    }

}

