//UPDATE POWERBAR
void updatePowerBar(UINT8 powerBarID)
{
    powerBarDisp[0] = 0x0F;
    powerBarDisp[1] = 0x0F;
    powerBarDisp[2] = 0x0F;

    switch (powerBarID) {
        //HIVE POWER
        case 1:
            tempVal = beehive;
            break;
        default:
            tempVal = shield;
            break;
    }

    m = tempVal >> 3;
    n = tempVal - (m << 3);

    for (l = 0; l < m; l++) {
        powerBarDisp[l] = 0x07;
    }

    powerBarDisp[l] = 0x0F - n;



    switch (powerBarID) {
        //HIVE POWER
        case 1:
            set_win_tiles(2, 1, 3, 1, powerBarDisp);
            break;

        default:
            set_win_tiles(16, 1, 3, 1, powerBarDisp);
            break;
    }


}

