//KILL PLAYER()
void killPlayer()
{
    stopAllSounds();
    pFrame = 24;
    //PLAY DEATH NOISE
    for (j = 0; j != 30; j++) {
        NR41_REG = DIV_REG;
        NR42_REG = 0x80 - j;
        NR43_REG = DIV_REG;
        NR44_REG = 0x80 + (DIV_REG % 6);
        BGP_REG = DIV_REG;
        wait_vbl_done();
    }

    BGP_REG = 0xE4;
    stopAllSounds();

    l = 128;
    m = 0;
    //FALL OFF SCREEN
    while (pY < 160) {
        if ((pY % 8) == 0) {
            m += 2;
            if (m == 8) {
                m = 0;
            }
        }
        pFrame = 80 + m;
        pY++;
        NR11_REG = DIV_REG;
        NR12_REG = 0xff;
        NR13_REG = 255 - pY;
        NR14_REG = 0x84;
        displayBee();
        wait_vbl_done();
    }

    stopAllSounds();
    pIFrames = 255;
}

//GAME OVER SCREEN
void endGame()
{
    demoModeTimer = 0;
    for (j = 0; j != 60; j++) {
        NR11_REG = DIV_REG;
        NR12_REG = 0xff - j;
        NR13_REG = DIV_REG;
        NR14_REG = 0x80 + (DIV_REG % 6);
        BGP_REG = DIV_REG;
        OBP0_REG = 255 - DIV_REG;
        wait_vbl_done();
    }

    pFrame = 2;
    stopAllSounds();
    fadeOutSprites(128);
    fadeOutBkg(128);
    hideAllSprites();
    scene = 7;
}

