//UPDATE PLAYERS SCORE IN REAL MODE, NOT DEMO MODE!
void incScore(UINT8 amt)
{
    if (scene == 2) {
        score[4] += amt;

        while (score[4] > 9) {
            score[4] -= 10;
            score[3]++;
        }
        while (score[3] > 9) {
            score[3] -= 10;
            score[2]++;

            round++;
            //      debugVal(0,0,round);
            switch ( round) {
                case 2:
                    maxEnemies = 4;
                    break;

                case 4:
                    maxEnemies = 5;
                    break;
                case 6:
                    maxEnemies = 6;
                    break;
            }
        }
        while (score[2] > 9) {
            score[2] -= 10;
            score[1]++;

        }
        while (score[1] > 9) {
            score[1] -= 10;
            score[0]++;
        }

        updateScore();
    }
}



//UPDATE OUR SCORE IN THE WINDOW
void updateScore()
{

    for (is = 0; is != 6; is++) {
        dispScore[is] = score[is] + 19;
    }

    set_win_tiles(8, 1, 6, 1, dispScore);
}


// COMPARE HIGH SCORES - START
void checkHigher()
{

    for (m = 0; m < 36; m += 6) {
        higher = 10;    // 10 = same score, 0 = lower than highscore, 20 = higher than highscore
        for (i = 0; i != 6; i++) {

            if (higher == 10) {
                n = m + i;
                hiScoreAll[n] -= 0xAA;

                if (hiScoreAll[n] > score[i]) {
                    higher = 0;
                }
                if (hiScoreAll[n] < score[i]) {
                    higher = 20;
                }
                if (hiScoreAll[n] == score[i]) {
                    higher = 10;
                }

                hiScoreAll[n] += 0xAA;
            }
        }

        //IF EQUAL TO OR HIGHER THAN CURRENT SCORE
        if (higher != 0) {

            //SHIFT ALL SCORES FROM THIS INDEX DOWN BY 6

            switch (m) {
                case 0:
                    ranking = 1;
                    break;
                case 6:
                    ranking = 2;
                    break;
                case 12:
                    ranking = 3;
                    break;
                case 18:
                    ranking = 4;
                    break;
                case 24:
                    ranking = 5;
                    break;
                case 30:
                    ranking = 6;
                    break;
                default:
                    ranking = 10;  //NOT RANKED
            }


            if (justACheck == 0) {
                for (n = 36; n != m; n--) {
                    hiScoreAll[(n + 6)] = hiScoreAll[n];
                }

                //INSERT PLAYER SCORE
                hiScoreAll[m] = score[0] + 0xAA;
                m++;
                hiScoreAll[m] = score[1] + 0xAA;;
                m++;
                hiScoreAll[m] = score[2] + 0xAA;;
                m++;
                hiScoreAll[m] = score[3] + 0xAA;;
                m++;
                hiScoreAll[m] = score[4] + 0xAA;;
                m++;
                hiScoreAll[m] = score[5] + 0xAA;;
                m = 90;

                tempVal = (ranking - 1) * 5;

                for (i = 25; i != tempVal; i--) {
                    initials[(i + 5)] = initials[i];
                }

                initials[tempVal] = tmpInitials[0];
                tempVal++;
                initials[tempVal] = tmpInitials[1];
                tempVal++;
                initials[tempVal] = tmpInitials[2];
                tempVal++;
                initials[tempVal] = tmpInitials[3];
                tempVal++;
                initials[tempVal] = tmpInitials[4];
            }
        }

    }

    //UNRANKED
    if (higher == 0) {
        ranking = 10;
        //rankingArray[0] = 0x63;
    }

}